'use strict';
/*jslint bitwise: true */

function _classCallCheck(instance, Constructor) { if (!(instance instanceof Constructor)) { throw new TypeError("Cannot call a class as a function"); } }

var modeMap = {
  exec: 64, //S_IXUSR
  dir: 16384, //S_IFDIR
  link: 40960, //S_IFLNK
  socket: 49152 //S_IFSOCK
};

module.exports = function FileMode(mode) {
  var _this = this;

  _classCallCheck(this, FileMode);

  Object.keys(modeMap).forEach(function (k) {
    _this[k] = Boolean(mode & modeMap[k]);
  });
};